// src/pages/StudentForm.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const StudentForm = () => {
  const { sessionId } = useParams();
  const [form, setForm] = useState({ name: '', rollNo: '' });
  const [status, setStatus] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`http://localhost:5000/api/attendance/submit/${sessionId}`, form);
      setStatus('✅ Attendance marked successfully');
    } catch (err) {
      setStatus(`❌ ${err.response?.data?.message || 'Error marking attendance'}`);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h2 className="text-xl font-bold mb-4">Student Attendance Form</h2>
      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded p-6 w-96">
        <input
          type="text"
          placeholder="Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          className="w-full mb-4 p-2 border rounded"
          required
        />
        <input
          type="text"
          placeholder="Roll No"
          value={form.rollNo}
          onChange={(e) => setForm({ ...form, rollNo: e.target.value })}
          className="w-full mb-4 p-2 border rounded"
          required
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded w-full">
          Submit Attendance
        </button>
      </form>
      {status && <p className="mt-4 text-center text-red-600">{status}</p>}
    </div>
  );
};

export default StudentForm;
