import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { QRCodeCanvas } from 'qrcode.react';

const TeacherDashboard = () => {
  const [teacher, setTeacher] = useState({});
  const [sessionId, setSessionId] = useState(null);
  const [form, setForm] = useState({ className: '', subject: '', hour: '', date: '' });
  const [students, setStudents] = useState([]);
  const [allSessions, setAllSessions] = useState([]);

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/teachers/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTeacher(res.data);
        fetchAllSessions(res.data._id);
      } catch (err) {
        console.error('Error fetching teacher:', err);
        alert('Authentication failed. Please login again.');
      }
    };
    fetchTeacher();
  }, [token]);

  useEffect(() => {
    let interval;
    if (sessionId) {
      interval = setInterval(async () => {
        try {
          const res = await axios.get(`http://localhost:5000/api/attendance/${sessionId}`);
          setStudents(res.data.students || []);
        } catch (err) {
          console.error('Error fetching students:', err);
        }
      }, 3000);
    }
    return () => clearInterval(interval);
  }, [sessionId]);

  const handleCreateSession = async () => {
    try {
      const res = await axios.post(
        'http://localhost:5000/api/attendance/create',
        { ...form, teacherId: teacher._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSessionId(res.data._id);
    } catch (err) {
      console.error('Error creating session:', err);
      alert('Failed to start attendance session.');
    }
  };

  const fetchAllSessions = async (teacherId) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/attendance/teacher/${teacherId}`);
      setAllSessions(res.data);
    } catch (err) {
      console.error('Error fetching sessions:', err);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold mb-4">Welcome, {teacher.name || 'Teacher'}</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-white p-4 rounded shadow">
          {sessionId ? (
            <>
              <h3 className="mb-2 font-semibold">Scan this QR to mark attendance</h3>
              <QRCodeCanvas
                value={`http://192.168.0.4:3000/attendance/${sessionId}`}
                size={256}
              />
            </>
          ) : (
            <>
              <h3 className="mb-2 font-semibold">Start New Attendance</h3>
              <input
                type="text"
                placeholder="Class"
                value={form.className}
                onChange={(e) => setForm({ ...form, className: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <input
                type="text"
                placeholder="Subject"
                value={form.subject}
                onChange={(e) => setForm({ ...form, subject: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <input
                type="text"
                placeholder="Hour"
                value={form.hour}
                onChange={(e) => setForm({ ...form, hour: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <input
                type="date"
                value={form.date}
                onChange={(e) => setForm({ ...form, date: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <button
                className="bg-green-600 text-white w-full py-2 rounded"
                onClick={handleCreateSession}
              >
                Generate QR
              </button>
            </>
          )}
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Students Present</h3>
          {students.length === 0 ? (
            <p>No students scanned yet.</p>
          ) : (
            <ul className="max-h-96 overflow-y-auto">
              {students.map((s, i) => (
                <li key={i} className="border-b py-1">
                  {s.name} ({s.rollNo})
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* History Section */}
      <div className="mt-8 bg-white p-4 rounded shadow">
        <h3 className="text-xl font-bold mb-4">Attendance History</h3>
        {allSessions.length === 0 ? (
          <p>No previous sessions found.</p>
        ) : (
          <div className="space-y-4">
            {allSessions.map((session) => (
              <div key={session._id} className="border p-2 rounded">
                <h4 className="font-semibold">{session.className} - {session.subject}</h4>
                <p className="text-sm text-gray-600">Date: {session.date} | Hour: {session.hour}</p>
                <ul className="ml-4 mt-2 list-disc">
                  {session.students.map((s, i) => (
                    <li key={i}>{s.name} ({s.rollNo})</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default TeacherDashboard;
