import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const TeacherLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/teachers/login', { email, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('teacherName', res.data.teacher.name);
      localStorage.setItem('teacherId', res.data.teacher._id);
      alert('Login successful!');
      navigate('/dashboard');
    } catch (err) {
      alert('Login failed: ' + (err.response?.data?.message || 'Unknown error'));
    }
  };

  return (
    <div className="teacher-login-container">
      <div className="teacher-login-box">
        <div className="teacher-login-header">
          <img src="/logo192.png" alt="Logo" className="login-logo" />
          <h2>Teacher Portal</h2>
        </div>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="login-input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="login-input"
            required
          />
          <button type="submit" className="login-button">Login</button>
        </form>
        <div className="text-center mt-4">
          <button onClick={() => navigate('/admin-login')} className="admin-nav-button">
            Go to Admin Login
          </button>
        </div>
      </div>
    </div>
  );
};

export default TeacherLogin;
