import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { QRCodeCanvas } from 'qrcode.react';

const TeacherDashboard = () => {
  const [teacher, setTeacher] = useState({});
  const [sessionId, setSessionId] = useState(null);
  const [form, setForm] = useState({ className: '', subject: '', hour: '', date: '' });
  const [students, setStudents] = useState([]);

  const token = localStorage.getItem('token');

  useEffect(() => {
    axios.get('http://localhost:5000/api/teacher/me', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setTeacher(res.data));
  }, []);

  const handleCreateSession = async () => {
    const res = await axios.post('http://localhost:5000/api/attendance/create', {
      ...form,
      teacherId: teacher._id
    });
    setSessionId(res.data._id);
    fetchStudents(res.data._id);
  };

  const fetchStudents = async (id) => {
    const interval = setInterval(async () => {
      const res = await axios.get(`http://localhost:5000/api/attendance/${id}`);
      setStudents(res.data.students);
    }, 3000);
    return () => clearInterval(interval);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Welcome, {teacher.name}</h2>

      <div className="grid grid-cols-2 gap-4">
        {/* Left: QR */}
        <div className="bg-white p-4 rounded shadow">
          {sessionId ? (
            <>
              <h3 className="mb-2 font-semibold">Scan this QR to mark attendance</h3>
              <QRCodeCanvas value={`http://localhost:3000/attendance/${sessionId}`} size={256} />
            </>
          ) : (
            <>
              <h3 className="mb-2 font-semibold">Start New Attendance</h3>
              <input type="text" placeholder="Class" value={form.className}
                onChange={(e) => setForm({ ...form, className: e.target.value })}
                className="w-full mb-2 p-2 border rounded" />
              <input type="text" placeholder="Subject" value={form.subject}
                onChange={(e) => setForm({ ...form, subject: e.target.value })}
                className="w-full mb-2 p-2 border rounded" />
              <input type="text" placeholder="Hour" value={form.hour}
                onChange={(e) => setForm({ ...form, hour: e.target.value })}
                className="w-full mb-2 p-2 border rounded" />
              <input type="date" value={form.date}
                onChange={(e) => setForm({ ...form, date: e.target.value })}
                className="w-full mb-2 p-2 border rounded" />
              <button className="bg-green-600 text-white w-full py-2 rounded" onClick={handleCreateSession}>
                Generate QR
              </button>
            </>
          )}
        </div>

        {/* Right: Students */}
        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Students Present</h3>
          <ul>
            {students.map((s, i) => (
              <li key={i} className="border-b py-1">{s.name} ({s.rollNo})</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default TeacherDashboard;
