import React, { useState } from 'react';
import QrReader from 'react-qr-reader';
import axios from 'axios';

const StudentScan = () => {
  const [scanResult, setScanResult] = useState(null);

  const handleScan = async (data) => {
    if (data) {
      setScanResult(data);
      try {
        await axios.post('http://localhost:5000/api/student/mark-attendance', {
          qrData: data,
          deviceId: window.navigator.userAgent
        });
        alert('Attendance marked');
      } catch (err) {
        alert('Attendance failed or already marked');
      }
    }
  };

  const handleError = (err) => console.error(err);

  return (
    <div className="p-6">
      <h1 className="text-xl mb-4">Scan the QR to mark attendance</h1>
      <QrReader delay={300} onError={handleError} onScan={handleScan} style={{ width: '100%' }} />
      {scanResult && <p>Scanned: {scanResult}</p>}
    </div>
  );
};

export default StudentScan;
