import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { QRCodeCanvas } from 'qrcode.react';

const TeacherDashboard = () => {
  const [teacher, setTeacher] = useState({});
  const [sessionId, setSessionId] = useState(null);
  const [form, setForm] = useState({ className: '', subject: '', hour: '', date: '' });
  const [students, setStudents] = useState([]);
  const [historySessions, setHistorySessions] = useState([]);

  const token = localStorage.getItem('teacherToken');

  useEffect(() => {
    const fetchTeacher = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/teachers/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTeacher(res.data);
      } catch (err) {
        alert('Login required. Please log in again.');
      }
    };
    fetchTeacher();
  }, [token]);

  useEffect(() => {
    if (sessionId) {
      const interval = setInterval(async () => {
        try {
          const res = await axios.get(`http://localhost:5000/api/attendance/${sessionId}`);
          setStudents(res.data.students || []);
        } catch (err) {
          console.error('Error fetching students:', err);
        }
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [sessionId]);

  useEffect(() => {
    if (teacher._id) fetchAttendanceHistory();
  }, [teacher]);

  const fetchAttendanceHistory = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/attendance/teacher/${teacher._id}`);
      setHistorySessions(res.data);
    } catch (err) {
      console.error('Failed to load history:', err);
    }
  };

  const handleCreateSession = async () => {
    const { className, subject, hour, date } = form;

    if (!className || !subject || !hour || !date) {
      alert('All fields are required');
      return;
    }

    try {
      const res = await axios.post(
        'http://localhost:5000/api/attendance/create',
        { ...form, teacherId: teacher._id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSessionId(res.data._id);
      setForm({ className: '', subject: '', hour: '', date: '' });
      fetchAttendanceHistory(); // refresh history after session creation
    } catch (err) {
      alert('Failed to start session');
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold mb-4">Welcome, {teacher.name || 'Teacher'}</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-8">
        <div className="bg-white p-4 rounded shadow">
          {sessionId ? (
            <>
              <h3 className="mb-2 font-semibold">QR Code - Live Session</h3>
              <QRCodeCanvas value={`http://192.168.0.4:3000/attendance/${sessionId}`} size={256} />
            </>
          ) : (
            <>
              <h3 className="mb-2 font-semibold">Start New Attendance</h3>
              <input
                type="text"
                placeholder="Class"
                value={form.className}
                onChange={(e) => setForm({ ...form, className: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <input
                type="text"
                placeholder="Subject"
                value={form.subject}
                onChange={(e) => setForm({ ...form, subject: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <input
                type="text"
                placeholder="Hour"
                value={form.hour}
                onChange={(e) => setForm({ ...form, hour: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <input
                type="date"
                value={form.date}
                onChange={(e) => setForm({ ...form, date: e.target.value })}
                className="w-full mb-2 p-2 border rounded"
                required
              />
              <button
                className="bg-green-600 text-white w-full py-2 rounded"
                onClick={handleCreateSession}
              >
                Generate QR
              </button>
            </>
          )}
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h3 className="font-semibold mb-2">Students Present</h3>
          {students.length === 0 ? (
            <p>No students scanned yet.</p>
          ) : (
            <ul className="max-h-96 overflow-y-auto">
              {students.map((s, i) => (
                <li key={i} className="border-b py-1">{s.name} ({s.rollNo})</li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* Attendance History */}
      <div className="bg-white p-4 rounded shadow">
        <h3 className="text-xl font-bold mb-4">📅 Attendance History</h3>
        {historySessions.length === 0 ? (
          <p>No previous sessions found.</p>
        ) : (
          <div className="space-y-4 max-h-[400px] overflow-y-auto">
            {historySessions.map((session) => (
              <div key={session._id} className="border rounded p-3">
                <p className="font-semibold">
                  {session.date} — {session.className} ({session.subject}) [{session.hour}]
                </p>
                <ul className="ml-4 mt-1 text-sm list-disc">
                  {session.students.length === 0 ? (
                    <li>No students</li>
                  ) : (
                    session.students.map((s, i) => (
                      <li key={i}>{s.name} ({s.rollNo})</li>
                    ))
                  )}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default TeacherDashboard;
