import React, { useState } from 'react';
import axios from 'axios';

const StudentForm = ({ sessionId }) => {
  const [form, setForm] = useState({ name: '', rollNo: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
     await axios.post(`http://${window.location.hostname}:5000/api/attendance/student/mark-attendance`, {
  qrData: `http://${window.location.hostname}:3000/attendance/${sessionId}`,
  name: form.name,
  rollNo: form.rollNo,
  deviceId: navigator.userAgent
});

      alert('Attendance marked successfully!');
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert('Error: ' + (err.response?.data?.message || 'Server error'));
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-white rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Fill Your Details</h2>
      <input
        type="text"
        placeholder="Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
        className="w-full mb-3 p-2 border rounded"
        required
      />
      <input
        type="text"
        placeholder="Roll No"
        value={form.rollNo}
        onChange={(e) => setForm({ ...form, rollNo: e.target.value })}
        className="w-full mb-3 p-2 border rounded"
        required
      />
      <button type="submit" className="w-full py-2 bg-green-600 text-white rounded">
        Submit Attendance
      </button>
    </form>
  );
};

export default StudentForm;
