import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [teachers, setTeachers] = useState([]);
  const [newTeacher, setNewTeacher] = useState({ name: '', email: '', password: '' });

  const navigate = useNavigate(); // ← Add this

  const fetchTeachers = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/teachers');
      setTeachers(res.data);
    } catch (err) {
      alert('Error fetching teachers');
    }
  };

  useEffect(() => {
    fetchTeachers();
  }, []);

  const handleAddTeacher = async (e) => {
    e.preventDefault();
    const { name, email, password } = newTeacher;

    if (!email.endsWith('@msrit.edu')) {
      alert('Please enter a valid @msrit.edu email address');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/teachers', { name, email, password });
      alert('Teacher added');
      setNewTeacher({ name: '', email: '', password: '' });
      fetchTeachers();
    } catch (err) {
      alert(err.response?.data?.message || 'Error adding teacher');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this teacher?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/teachers/${id}`);
      alert('Teacher deleted');
      fetchTeachers();
    } catch (err) {
      alert('Error deleting teacher');
    }
  };

  return (
    <div className="admin-dashboard-page">
      <img src="https://d2e9h3gjmozu47.cloudfront.net/brand.png" alt="Logo" className="admin-logo" />

      {/* ✅ Go to Teacher Login Button */}
      <div className="go-login-btn-container">
        <button className="go-login-btn" onClick={() => navigate('/teacher-login')}>
          Go to Teacher Login
        </button>
      </div>

      <div className="admin-dashboard-wrapper">
        {/* Existing Add Form and Teacher List */}
        {/* ... */}
      </div>
    </div>
  );
};

export default AdminDashboard;
